// by Nikos Papadopoulos, 4rknova / 2020
// Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

#define PI	(3.14159265359)
#define PIH (1.57079632679)

vec3 dir(vec2 p)
{
    vec2 s = (p/iResolution.xy*2.-1.) * vec2(PI,PIH); // Spherical coords
    vec4 t = vec4(sin(s), cos(s));                    // Precalculated sin/cos values
    return t.zyx * vec3(t.w, 1, t.w);                 // Conversion to direction
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	fragColor = texture(iChannel0, dir(fragCoord));
}

// by Nikos Papadopoulos, 4rknova / 2020
// Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

#define FACE_X_POS (0)
#define FACE_X_NEG (1)
#define FACE_Y_POS (2)
#define FACE_Y_NEG (3)
#define FACE_Z_POS (4)
#define FACE_Z_NEG (5)

int dir2face(in vec3 dir)
{
    vec3 d = abs(dir);
    if (d.z >= d.x && d.z >= d.y) return dir.z < 0.0 ? FACE_Z_NEG : FACE_Z_POS;
    if (d.y >= d.x              ) return dir.y < 0.0 ? FACE_Y_NEG : FACE_Y_POS;
    							  return dir.x < 0.0 ? FACE_X_NEG : FACE_X_POS;  
}

void mainCubemap( out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir )
{
    int idx = dir2face(rayDir);    

    vec3 col = vec3(0);

    switch (idx) {
        case FACE_X_POS: col = vec3(1.0,0.0,0.0); break;
        case FACE_X_NEG: col = vec3(0.5,0.0,0.0); break;
        case FACE_Y_POS: col = vec3(0.0,1.0,0.0); break;
        case FACE_Y_NEG: col = vec3(0.0,0.5,0.0); break;
        case FACE_Z_POS: col = vec3(0.0,0.0,1.0); break;
        case FACE_Z_NEG: col = vec3(0.0,0.0,0.5); break;
    }

    fragColor = vec4(col, 1);
}
